import db from '../config/db.js';

// 1. Trả về danh sách tài khoản
export const getAccounts = async (req, res) => {
    try {
        const [accounts] = await db.execute('SELECT * FROM users');
        res.json(accounts);
    } catch (error) {
        res.status(500).send('Lỗi khi lấy danh sách tài khoản');
    }
};

// 2. Trả về thông tin tài khoản cụ thể
export const getAccountById = async (req, res) => {
    const { id } = req.params;
    try {
        const [[account]] = await db.execute('SELECT * FROM users WHERE id = ?', [id]);
        if (account) {
            res.json(account);
        } else {
            res.status(404).send('Tài khoản không tồn tại');
        }
    } catch (error) {
        res.status(500).send('Lỗi khi lấy thông tin tài khoản');
    }
};

